# backend/threat_detection/email_scanner.py

import re

class EmailScanner:
    def __init__(self):
        self.phishing_keywords = ['urgent', 'verify your account', 'click here', 'update your information']

    def is_phishing(self, email_body):
        return any(keyword in email_body.lower() for keyword in self.phishing_keywords)

    def scan_email(self, email):
        if self.is_phishing(email['body']):
            return True, "Phishing attempt detected."
        return False, "No threats detected."
